import React, { Component } from 'react';
import Pokedex from './Pokedex';

class Pokegame extends Component {
    static defaultProps = {
        pokemon: [
            {id: 4, name: 'Charmander', type: 'fire', base_experience: 62},
            {id: 7, name: 'Squirtle', type: 'water', base_experience: 63},
            {id: 11, name: 'Metapod', type: 'bug', base_experience: 72},
            {id: 12, name: 'Butterfree', type: 'flying', base_experience: 178},
            {id: 25, name: 'Pikachu', type: 'electric', base_experience: 112},
            {id: 39, name: 'Jigglypuff', type: 'normal', base_experience: 95},
            {id: 94, name: 'Gengar', type: 'poison', base_experience: 225},
            {id: 133, name: 'Eevee', type: 'normal', base_experience: 65}
          ]
    };
    render() {
        let hand1 = [];
        let hand2 = [ ...this.props.pokemon ];
        while (hand1.length < hand2.length) {
            let randomIdx = Math.floor(Math.random() * hand2.length);
            let randomPokemon = hand2.splice(randomIdx, 1)[0];
            hand1.push(randomPokemon);
        }
        let experience1 = hand1.reduce((experience, pokemon) => experience + pokemon.base_experience, 0);
        let experience2 = hand2.reduce((experience, pokemon) => experience +pokemon.base_experience, 0);
        return (
            <div>
                <h1>Pokegame!</h1>
                <Pokedex pokemon={hand1} experience={experience1} />
                <Pokedex pokemon={hand2} experience={experience2}/>
            </div>
        )
    }
}

export default Pokegame;
